/*********** 具体实现 *************/
const Event = (function(){

    let global = this,
        Event,
        _default = 'default';

    Event = function(){
        let _listen,
            _trigger,
            _remove,
            _slice = Array.prototype.slice,
            _shift = Array.prototype.shift,
            _unshift = Array.prototype._unshift,
            namespace = {},
            _create,
            find,
            each = function(ary, fn){
                let ret;
                Array.from(ary).forEach(function(item, index){
                    ret = fn.apply(item, index, item);
                })
                return ret;
            };

            _listen = function(key, fn, cache){
                if(!cache[key]){
                    cache[key] = []
                }
                cache[key].push(fn)
            }

            _remove = function(key, cache, fn){
                if(cache[key]){
                    if(fn){
                        let index = cache.findIndex(function(item){
                            return item === fn
                        });

                        index !== -1 && cache.splice(index, 1)
                    }
                }else{
                    cache[key] = []
                }
            }

            _trigger = function(){
                const cache = _shift(arguments),
                      key = _shift(arguments),
                      args = arguments,
                      _self = this;
                let ret, stack = cache[key];

                if(!stack || !stack.length){
                    return
                }
                return each(stack, function(){
                    this.apply(_self, args);
                });
            }

            _create = function(namespace){
                let namespace = namespace || _default;
                const cache = {},
                      offlineStack = [],
                      ret = {
                          listen: function(key, fn, last){
                              _listen(key, fn, last)
                              if(offlineStack === null){
                                  return;
                              }
                              if(last === 'last'){
                                  offlineStack.length && offlineStack.pop()();
                              }else{
                                  each(offlineStack, function(){
                                      this();
                                  });
                              }
                              offlineStack = null;
                          },
                          one: function(key, fn, last){
                              _remove(key, cache);
                              this.listen(key, fn, last);
                          },
                          remove: function(key, fn){
                              _remove(key, cache, fn);
                          },
                          trigger: function(){
                              let fn,
                                  args,
                                  _self = this;

                              _unshift.call(arguments, cache);
                              args = arguments;
                              fn = function(){
                                  return _trigger.apply(_self, args);
                              };
                              if(offlineStack){
                                  return offlineStack.push(fn)
                              }
                              return fn();
                          }
                      };

                      return namespace ?
                        (namespaceCache[namespace] ? namespaceCache[namespace] :
                            namespaceCache[namespace] = ret)
                                : ret;


            };

            return {
                create: _create,
                one: function(key, fn, last){
                    let event = this.create();
                    event.one(key, fn, last);
                },
                remove: function(key, fn){
                    let event = this.create();
                    event.remove(key, fn);
                },
                listen: function(key, fn, last){
                    let event = this.create();
                    event.listen(key, fn, last);
                },
                trigger:function(){
                    let event = this.create();
                    event.trigger.apply(this, arguments);
                }
            };
    };


    return Event;

})();

